mapper.select:

   select * from
(select a.*,rownum r from(select * 
from u_ask_planit
where id=#{id}
order by askdate desc)a)
where  r between #{start} and #{end}

select L.*,R.*
from review R INNER JOIN liked_review L on R.review_id=L.review_id
where rownum<=5 and id=#{id}

select L.*,R.*
from review R INNER JOIN liked_review L on R.review_id=L.review_id
where id=#{id}

select profile
from member
where id=#{id}
